import { createRoot }  from "react-dom/client"
import { useState } from "react"

// Importing Links component.
import Links from "./components/Links";
import HomeMessage from "./components/HomeMessage";

const MainComponent = () => {
    return (
        <div>
            {/* Encapsulating everything, hope nothing breaks. */}
            <nav class="nav-bar">
                <a class="nav-item" id="current-page" href="index.html">Home Page</a>
                <a class="nav-item" href="about.html">About Page</a>
                <a class="nav-item" href="portfolio.html">Portfolio</a>
            </nav>

            {/* Main content of the page. */}
            <div class="home-content">
                <HomeMessage />

                <Links />
            </div>

            {/* Footer used across all pages */}

        </div>
    )
}

let appEle = document.getElementById("app");
let root = createRoot(appEle);
root.render(<MainComponent/>)
